head.script:
/**/
			if ('serviceWorker' in navigator && true) {
			navigator.serviceWorker.register('/sw.js')
			.then(function(reg) {
			console.log('SW Registration succeeded. Scope is ' + reg.scope);
			})
			.catch(function(error) {
			console.log('SW Registration failed with ' + error);
			});
			}

/**/

